    def selectedDLC(self, btn, dlc):


        if btn.isChecked():
            if dlc not in self.sorter.selected_dlcs:
                self.sorter.selected_dlcs.append(dlc)
        else:
            if dlc in self.sorter.selected_dlcs:
                self.sorter.selected_dlcs.remove(dlc)

        data = self.sorter.sort_by_dlc()
        # emitujemy najnowsze filtrowane dane
        self.data_updated.emit(data)
